import React, { useState } from 'react';

const DriverDashboard = () => {
  const [isOnline, setIsOnline] = useState(false);
  const [currentLocation, setCurrentLocation] = useState('');

  // Mock ride requests data
  const [rideRequests] = useState([
    {
      id: 1,
      pickup: '123 Main St',
      dropoff: '456 Market St',
      passengers: 2,
      distance: '3.2 miles',
      fare: '$15.50'
    }
  ]);

  const toggleOnlineStatus = () => {
    setIsOnline(!isOnline);
  };

  const handleLocationUpdate = (e) => {
    setCurrentLocation(e.target.value);
  };

  const acceptRide = (rideId) => {
    // TODO: Implement ride acceptance logic
    console.log('Accepting ride:', rideId);
  };

  return (
    <div className="max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">Driver Dashboard</h2>

      <div className="bg-white p-6 rounded-lg shadow-md mb-6">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h3 className="text-xl font-semibold">Driver Status</h3>
            <p className={`text-lg ${isOnline ? 'text-green-600' : 'text-red-600'}`}>
              {isOnline ? 'Online' : 'Offline'}
            </p>
          </div>
          <button
            onClick={toggleOnlineStatus}
            className={`px-4 py-2 rounded ${
              isOnline
                ? 'bg-red-500 hover:bg-red-600'
                : 'bg-green-500 hover:bg-green-600'
            } text-white`}
          >
            {isOnline ? 'Go Offline' : 'Go Online'}
          </button>
        </div>

        <div>
          <label className="block mb-1">Current Location</label>
          <input
            type="text"
            value={currentLocation}
            onChange={handleLocationUpdate}
            className="w-full p-2 border rounded"
            placeholder="Update your current location"
          />
        </div>
      </div>

      <div className="bg-white p-6 rounded-lg shadow-md">
        <h3 className="text-xl font-semibold mb-4">Available Ride Requests</h3>
        {rideRequests.length > 0 ? (
          <div className="space-y-4">
            {rideRequests.map((ride) => (
              <div key={ride.id} className="border p-4 rounded-lg">
                <div className="grid grid-cols-2 gap-4 mb-4">
                  <div>
                    <p className="font-semibold">Pickup</p>
                    <p>{ride.pickup}</p>
                  </div>
                  <div>
                    <p className="font-semibold">Dropoff</p>
                    <p>{ride.dropoff}</p>
                  </div>
                  <div>
                    <p className="font-semibold">Passengers</p>
                    <p>{ride.passengers}</p>
                  </div>
                  <div>
                    <p className="font-semibold">Estimated Fare</p>
                    <p>{ride.fare}</p>
                  </div>
                </div>
                <button
                  onClick={() => acceptRide(ride.id)}
                  className="w-full bg-black text-white p-2 rounded hover:bg-gray-800"
                >
                  Accept Ride
                </button>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-500 text-center">No ride requests available</p>
        )}
      </div>
    </div>
  );
};

export default DriverDashboard;
