import React, { useState } from 'react';

const RideRequest = () => {
  const [rideData, setRideData] = useState({
    pickup: '',
    dropoff: '',
    passengers: 1,
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: Implement ride request logic
    console.log('Ride request:', rideData);
  };

  const handleChange = (e) => {
    setRideData({
      ...rideData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="max-w-md mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">Request a Ride</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block mb-1">Pickup Location</label>
          <input
            type="text"
            name="pickup"
            value={rideData.pickup}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            placeholder="Enter pickup location"
            required
          />
        </div>
        <div>
          <label className="block mb-1">Dropoff Location</label>
          <input
            type="text"
            name="dropoff"
            value={rideData.dropoff}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            placeholder="Enter destination"
            required
          />
        </div>
        <div>
          <label className="block mb-1">Number of Passengers</label>
          <input
            type="number"
            name="passengers"
            value={rideData.passengers}
            onChange={handleChange}
            min="1"
            max="4"
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <button
          type="submit"
          className="w-full bg-black text-white p-2 rounded hover:bg-gray-800"
        >
          Request Ride
        </button>
      </form>
    </div>
  );
};

export default RideRequest;
