import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="bg-black">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <div className="flex items-center">
            <Link to="/" className="flex-shrink-0">
              <span className="text-white text-2xl font-bold">RideConnect</span>
            </Link>
          </div>
          <div className="hidden md:block">
            <div className="ml-10 flex items-baseline space-x-4">
              <Link to="/" className="text-gray-300 hover:text-white px-3 py-2 rounded-md text-sm font-medium">Home</Link>
              <Link to="/request-ride" className="text-gray-300 hover:text-white px-3 py-2 rounded-md text-sm font-medium">Request Ride</Link>
              <Link to="/driver/dashboard" className="text-gray-300 hover:text-white px-3 py-2 rounded-md text-sm font-medium">Drive</Link>
              <Link to="/login" className="text-gray-300 hover:text-white px-3 py-2 rounded-md text-sm font-medium">Login</Link>
              <Link to="/register" className="bg-white text-black px-3 py-2 rounded-md text-sm font-medium hover:bg-gray-200">Sign up</Link>
            </div>
          </div>
          <div className="md:hidden">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-white hover:bg-gray-700 focus:outline-none"
            >
              {isOpen ? (
                <XMarkIcon className="block h-6 w-6" />
              ) : (
                <Bars3Icon className="block h-6 w-6" />
              )}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile menu */}
      {isOpen && (
        <div className="md:hidden">
          <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
            <Link to="/" className="text-gray-300 hover:text-white block px-3 py-2 rounded-md text-base font-medium">Home</Link>
            <Link to="/request-ride" className="text-gray-300 hover:text-white block px-3 py-2 rounded-md text-base font-medium">Request Ride</Link>
            <Link to="/driver/dashboard" className="text-gray-300 hover:text-white block px-3 py-2 rounded-md text-base font-medium">Drive</Link>
            <Link to="/login" className="text-gray-300 hover:text-white block px-3 py-2 rounded-md text-base font-medium">Login</Link>
            <Link to="/register" className="bg-white text-black block px-3 py-2 rounded-md text-base font-medium">Sign up</Link>
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
